package exemplofor;

public class Exemplo03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 0; i <= 20; i += 2) {
			
			System.out.print(i + ", ");
			}
		
		System.out.println();
		System.out.println("Contagem regressiva:");
		
		for(int i = 10; i>= 0; i--) {
			System.out.println(i);
		}
		System.out.println("Ignição!!!");
	}

}
